import axios from "axios";

const All = ({ posts }) => {
  const [ariticle, setArticls] = useState([]);

  useEffect(() => {
    function getPosts() {
      axios
        .get("http://localhost:8040/Product/")
        .then((res) => {
          setArticls(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          alert(err.message);
        });
    }
    getPosts();
  }, [ariticle]);

  return (
    <div>
