import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const All = ({ posts }) => {
  const [ariticle, setArticls] = useState([]);
  const [serQuery, setQuery] = useState("");

  useEffect(() => {
    function getPosts() {
      axios
        .get("http://localhost:8040/Product/")
        .then((res) => {
          setArticls(res.data);
          console.log(res.data);
        })
        .catch((err) => {
          alert(err.message);
        });
    }
    getPosts();
  }, [ariticle]);

  return (
    <div>
      {/* {ariticle.map((ariticl) => (
        <div>
          <br></br>
          <img
            src={require(`../../Backend/uploads/${ariticl.image}`)}
            width={50}
          ></img>
          <br></br>

          {ariticl.image}
          <br></br>
          {ariticl.name}
          <br></br>
        </div>
      ))} */}
      <table
        className="table"
        style={{ marginTop: "40px", width: "1200px", marginLeft: "200px" }}
      >
        <thead class="thead-dark">
          {ariticle && (
            <tr>
              <th scope="col">Name</th>
              <th scope="col">Price </th>
              <th scope="col">catogory</th>
              <th scope="col">description</th>
              <th scope="col">image </th>
            </tr>
          )}
        </thead>

        <tbody>
          {ariticle &&
            ariticle
              .filter(
                (e) =>
                  e.name.toLowerCase().includes(serQuery) ||
                  e.name.includes(serQuery) ||
                  e.address.toLowerCase().includes(serQuery) ||
                  e.address.includes(serQuery) ||
                  e.NIC.toLowerCase().includes(serQuery) ||
                  e.NIC.includes(serQuery)
              )
              .map((e, i) => (
                <tr>
                  <td>{e.name}</td>
                  <td>{e.price}</td>
                  <td>{e.catogory}</td>
                  <td>{e.description}</td>
                  <td>{e.image}</td>
                </tr>
              ))}
        </tbody>
      </table>
    </div>
  );
};
export default All;
